    private void addGroup_btnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addGroup_btnActionPerformed
        AddGroup_Controller controller = new AddGroup_Controller();
        try{
            controller.addGroup(this);
            controller.addGroupShowSucceed(this);
        }
        catch(Exception ex){
            controller.addGroupShowError(ex,this);
        }
    }//GEN-LAST:event_addGroup_btnActionPerformed

