    //Shows the current existing groups in a list, gets it from the controller
    private DefaultListModel createNullGroupsModel(){
        data = new DefaultListModel();
        AddGroup_Controller cont = new AddGroup_Controller();
        
        for(int i=0;i < cont.getNullGroupsList().size(); i++){
            data.addElement(cont.getNullGroupsList().get(i));
        }
        return data;
    }
